import React from "react";
import "./About.css";

const About = () => {

  const handleScroll = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: "smooth" });
    }
  };


  return (
    <section id="about">

      <div className="about-header">
        <h2 data-aos="fade-in" >ABOUT ME</h2>
        <div className="underline"></div>
        <p>
          Here you will find more information about me, what I do, and my
          current skills mostly in terms of programming and technology.ðŸ“Œ
        </p>
      </div>


      <div className="about-content">

        <div className="about-text" data-aos="fade-in">
          <h3>Get to know me!</h3>
          <p>
            I'm a <strong>Frontend Focused Web Developer</strong> building and
            managing the Front-end of Websites and Web Applications that leads
            to the success of the overall product. Check out some of my work in
            the <strong><a onClick={()=>handleScroll("projects")}>Projects</a></strong> section.
          </p>
          <p>
            I also like sharing content related to the stuff that I have learned
            over the years in <strong>Web Development</strong> so it can help
            other people of the Dev Community. Feel free to connect with me on{" "}
            <a href="https://www.linkedin.com/in/pauras-lokhande-681210301">LinkedIn</a> and <a href="https://www.instagram.com/pauras_281/">Instagram</a>.
          </p>
          <p>
            I'm open to <strong>Job </strong>opportunities where I can
            contribute, learn and grow. If you have a good opportunity that
            matches my skills and experience then don't hesitate to{" "}
            <strong><a onClick={()=>handleScroll("contact")}>contact</a></strong>&nbsp;me.
          </p>
        </div>


        <div className="skills-section">
          <h3>My Skills</h3>
          <div className="skills-grid">
            {[
              {
                name: "HTML",
                image:
                  "data:image/jpeg;base64,",
              },
              {
                name: "CSS",
                image:
                  "data:image/png;base64,",
              },
              {
                name: "Javascript",
                image:
                  "data:image/png;base64,",
              },
              { name: "NodeJS", image: "https://logodix.com/logo/1764972.png" },
              {
                name: "ReactJS",
                image:
                  "http://logos-download.com/wp-content/uploads/2016/09/React_logo_logotype_emblem.png",
              },
              {
                name: "React Native",
                image:
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSIh-MNjhBkw8LzajLfPLn3VxvX7G799k8hPQ&s",
              },
              {
                name: "NextJS",
                image:
                "data:image/png;base64,",
              },
              {
                name: "C++",
                image:
                  "http://blog.desafiolatam.com/wp-content/uploads/2018/05/c-logo.png",
              },
              {
                name: "Bootstrap",
                image:
                "data:image/jpeg;base64,",
              },
              {
                name: "TailwindCSS",
                image:
                "https://w7.pngwing.com/pngs/293/485/png-transparent-tailwind-css-hd-logo.png",
              },
              {
                name: "MySQL",
                image:
                "data:image/png;base64,",
              },
              {
                name: "MongoDB",
                image:
                "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSITHn_TgjDyhdWvePNw0mveDrTUr00GLfv_Q&s",
              },
              {
                name: "MERN stack",
                image:
                  "data:image/png;base64,",
              },
              {
                name: "GitHub",
                image:
                "https://w7.pngwing.com/pngs/646/324/png-transparent-github-computer-icons-github-logo-monochrome-head-thumbnail.png",
              },
              {
                name: "ChatGPT",
                image:
                "data:image/png;base64,",
              }
            ].map((skill, index) => (
              <div key={index} className="skill-box" data-aos={index % 2 === 0 ? 'fade-left' : 'fade-right'} >
                <img src={skill.image} alt="" className="skill-img" />
                <p className="skill-text">&nbsp;{skill.name}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
